pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./PriceOracle.sol";

contract VaultManager is Ownable {
    using SafeERC20 for IERC20;

    PriceOracle public oracle;

    // user => token => amount
    mapping(address => mapping(address => uint256)) public collateral;

    event OracleUpdated(address indexed oracle);
    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);

    constructor(PriceOracle _oracle) Ownable(msg.sender) {
        oracle = _oracle;
        emit OracleUpdated(address(_oracle));
    }

    function setOracle(PriceOracle _oracle) external onlyOwner {
        require(address(_oracle) != address(0), "VaultManager: zero oracle");
        oracle = _oracle;
        emit OracleUpdated(address(_oracle));
    }

    function depositCollateral(address token, uint256 amount) external {
        require(token != address(0), "VaultManager: token zero");
        require(amount > 0, "VaultManager: amount zero");
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        collateral[msg.sender][token] += amount;
        emit CollateralDeposited(msg.sender, token, amount);
    }

    function withdrawCollateral(address token, uint256 amount) external {
        require(token != address(0), "VaultManager: token zero");
        require(amount > 0, "VaultManager: amount zero");
        uint256 bal = collateral[msg.sender][token];
        require(bal >= amount, "VaultManager: insufficient collateral");
        collateral[msg.sender][token] = bal - amount;
        IERC20(token).safeTransfer(msg.sender, amount);
        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    function getUserTokenCollateral(address user, address token) external view returns (uint256) {
        return collateral[user][token];
    }

    // Returns value in oracle's price decimals (assumed 1e18)
    function getCollateralValue(address user, address token) external view returns (uint256) {
        uint256 amt = collateral[user][token];
        if (amt == 0) return 0;
        uint256 px = oracle.getPrice(token);
        return amt * px / 1e18;
    }
}

