// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IUniversalOracle.sol";

/**
 * @title ChainlinkOracle
 * @notice Oracle implementation for Chainlink price feeds on Ethereum
 * @dev Integrates with Chainlink's decentralized oracle network
 */
contract ChainlinkOracle is IUniversalOracle, Ownable {
    /// @notice Price staleness threshold (1 hour for Chainlink)
    uint256 public constant PRICE_STALENESS_THRESHOLD = 1 hours;

    /// @notice Mapping of token to Chainlink aggregator address
    mapping(address => address) public tokenToAggregator;

    error ChainlinkOracle__PriceNotSet();
    error ChainlinkOracle__StalePrice();
    error ChainlinkOracle__InvalidPrice();
    error ChainlinkOracle__AggregatorNotSet();

    event AggregatorSet(address indexed token, address indexed aggregator);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set Chainlink aggregator for a token
     * @param token Token address
     * @param aggregator Chainlink price feed aggregator address
     */
    function setAggregator(address token, address aggregator) external onlyOwner {
        tokenToAggregator[token] = aggregator;
        emit AggregatorSet(token, aggregator);
    }

    /**
     * @notice Get the latest price for a token from Chainlink
     * @param token Token address
     * @return price Price with 18 decimals
     * @return timestamp Timestamp of last update
     */
    function getPrice(address token) external view override returns (uint256 price, uint256 timestamp) {
        address aggregator = tokenToAggregator[token];
        if (aggregator == address(0)) revert ChainlinkOracle__AggregatorNotSet();

        // Chainlink aggregator interface
        (, int256 answer, , uint256 updatedAt, ) = AggregatorV3Interface(aggregator).latestRoundData();

        if (answer <= 0) revert ChainlinkOracle__InvalidPrice();
        if (block.timestamp - updatedAt > PRICE_STALENESS_THRESHOLD) {
            revert ChainlinkOracle__StalePrice();
        }

        // Convert to 18 decimals (Chainlink typically uses 8 decimals)
        uint8 decimals = AggregatorV3Interface(aggregator).decimals();
        uint256 adjustedPrice = uint256(answer) * (10 ** (18 - decimals));

        return (adjustedPrice, updatedAt);
    }

    /**
     * @notice Check if token has valid price feed
     * @param token Token address
     * @return hasPrice True if valid price exists
     */
    function hasPrice(address token) external view override returns (bool) {
        address aggregator = tokenToAggregator[token];
        if (aggregator == address(0)) return false;

        try AggregatorV3Interface(aggregator).latestRoundData() returns (
            uint80, int256 answer, uint256, uint256 updatedAt, uint80
        ) {
            return answer > 0 && (block.timestamp - updatedAt <= PRICE_STALENESS_THRESHOLD);
        } catch {
            return false;
        }
    }

    /**
     * @notice Get price staleness threshold
     * @return threshold Staleness threshold in seconds
     */
    function getPriceStalenessThreshold() external pure override returns (uint256 threshold) {
        return PRICE_STALENESS_THRESHOLD;
    }
}

/**
 * @title AggregatorV3Interface
 * @notice Interface for Chainlink price feed aggregators
 */
interface AggregatorV3Interface {
    function decimals() external view returns (uint8);
    function latestRoundData() external view returns (
        uint80 roundId,
        int256 answer,
        uint256 startedAt,
        uint256 updatedAt,
        uint80 answeredInRound
    );
}

contracts/oracles/IUniversalOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IUniversalOracle
 * @notice Universal interface for price oracles supporting multiple providers
 * @dev Standardizes price feed access across different oracle implementations
 */
interface IUniversalOracle {
    /**
     * @notice Get the latest price for a token
     * @param token The token address to get price for
     * @return price The price with 18 decimals
     * @return timestamp The timestamp of the price update
     */
    function getPrice(address token) external view returns (uint256 price, uint256 timestamp);

    /**
     * @notice Check if a token has a valid price feed
     * @param token The token address to check
     * @return hasPrice True if token has a valid price feed
     */
    function hasPrice(address token) external view returns (bool hasPrice);

    /**
     * @notice Get the price staleness threshold
     * @return threshold Maximum age of price data in seconds
     */
    function getPriceStalenessThreshold() external view returns (uint256 threshold);

    /**
     * @notice Emitted when a price is updated
     * @param token The token address
     * @param price The new price
     * @param timestamp The update timestamp
     */
    event PriceUpdated(address indexed token, uint256 price, uint256 timestamp);
}

contracts/oracles/OracleManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IUniversalOracle.sol";

/**
 * @title OracleManager
 * @notice Manages multiple oracle sources with fallback mechanisms
 * @dev Provides unified price feed interface with redundancy and security
 */
contract OracleManager is Ownable {
    /// @notice Primary oracle (RedStone for Lisk, Chainlink for Ethereum)
    IUniversalOracle public primaryOracle;

    /// @notice Secondary oracle (fallback)
    IUniversalOracle public secondaryOracle;

    /// @notice Manual oracle (emergency fallback)
    IUniversalOracle public manualOracle;

    /// @notice Maximum price deviation between oracles (10%)
    uint256 public constant MAX_PRICE_DEVIATION = 1000; // 10% in basis points
    uint256 public constant BASIS_POINTS = 10000;

    error OracleManager__NoPriceAvailable();
    error OracleManager__PriceDeviationTooHigh();
    error OracleManager__InvalidOracle();

    event PrimaryOracleSet(address indexed oracle);
    event SecondaryOracleSet(address indexed oracle);
    event ManualOracleSet(address indexed oracle);
    event PriceRetrieved(address indexed token, uint256 price, uint8 oracleUsed);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Set primary oracle
     * @param oracle Oracle contract address
     */
    function setPrimaryOracle(address oracle) external onlyOwner {
        if (oracle == address(0)) revert OracleManager__InvalidOracle();
        primaryOracle = IUniversalOracle(oracle);
        emit PrimaryOracleSet(oracle);
    }

    /**
     * @notice Set secondary oracle
     * @param oracle Oracle contract address
     */
    function setSecondaryOracle(address oracle) external onlyOwner {
        if (oracle == address(0)) revert OracleManager__InvalidOracle();
        secondaryOracle = IUniversalOracle(oracle);
        emit SecondaryOracleSet(oracle);
    }

    /**
     * @notice Set manual oracle
     * @param oracle Oracle contract address
     */
    function setManualOracle(address oracle) external onlyOwner {
        if (oracle == address(0)) revert OracleManager__InvalidOracle();
        manualOracle = IUniversalOracle(oracle);
        emit ManualOracleSet(oracle);
    }

    /**
     * @notice Get price with fallback mechanism
     * @param token Token address
     * @return price Price with 18 decimals
     */
    function getPrice(address token) external returns (uint256 price) {
        // Try primary oracle first
        if (address(primaryOracle) != address(0)) {
            try primaryOracle.getPrice(token) returns (uint256 primaryPrice, uint256) {
                if (primaryPrice > 0) {
                    emit PriceRetrieved(token, primaryPrice, 1);
                    return primaryPrice;
                }
            } catch {}
        }

        // Try secondary oracle
        if (address(secondaryOracle) != address(0)) {
            try secondaryOracle.getPrice(token) returns (uint256 secondaryPrice, uint256) {
                if (secondaryPrice > 0) {
                    emit PriceRetrieved(token, secondaryPrice, 2);
                    return secondaryPrice;
                }
            } catch {}
        }

        // Try manual oracle as last resort
        if (address(manualOracle) != address(0)) {
            try manualOracle.getPrice(token) returns (uint256 manualPrice, uint256) {
                if (manualPrice > 0) {
                    emit PriceRetrieved(token, manualPrice, 3);
                    return manualPrice;
                }
            } catch {}
        }

        revert OracleManager__NoPriceAvailable();
    }

    /**
     * @notice Get price with validation between multiple oracles
     * @param token Token address
     * @return price Validated price with 18 decimals
     */
    function getValidatedPrice(address token) external returns (uint256 price) {
        uint256 primaryPrice = 0;
        uint256 secondaryPrice = 0;
        bool hasPrimary = false;
        bool hasSecondary = false;

        // Get primary price
        if (address(primaryOracle) != address(0)) {
            try primaryOracle.getPrice(token) returns (uint256 p, uint256) {
                if (p > 0) {
                    primaryPrice = p;
                    hasPrimary = true;
                }
            } catch {}
        }

        // Get secondary price
        if (address(secondaryOracle) != address(0)) {
            try secondaryOracle.getPrice(token) returns (uint256 s, uint256) {
                if (s > 0) {
                    secondaryPrice = s;
                    hasSecondary = true;
                }
            } catch {}
        }

        // Validate prices if both available
        if (hasPrimary && hasSecondary) {
            uint256 deviation = _calculateDeviation(primaryPrice, secondaryPrice);
            if (deviation > MAX_PRICE_DEVIATION) {
                revert OracleManager__PriceDeviationTooHigh();
            }
            return primaryPrice; // Use primary if validation passes
        }

        // Return available price or fallback to manual
        if (hasPrimary) return primaryPrice;
        if (hasSecondary) return secondaryPrice;

        // Last resort: manual oracle
        return this.getPrice(token);
    }

    /**
     * @notice Calculate percentage deviation between two prices
     * @param price1 First price
     * @param price2 Second price
     * @return deviation Deviation in basis points
     */
    function _calculateDeviation(uint256 price1, uint256 price2) internal pure returns (uint256 deviation) {
        uint256 diff = price1 > price2 ? price1 - price2 : price2 - price1;
        uint256 average = (price1 + price2) / 2;
        return (diff * BASIS_POINTS) / average;
    }
}

contracts/oracles/RedStoneOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./IUniversalOracle.sol";

/**
 * @title RedStoneOracle
 * @notice Oracle implementation for RedStone price feeds on Lisk
 * @dev Integrates with RedStone's modular oracle infrastructure
 */
contract RedStoneOracle is IUniversalOracle, Ownable {
    /// @notice Price staleness threshold (24 hours)
    uint256 public constant PRICE_STALENESS_THRESHOLD = 24 hours;

    /// @notice Mapping of token to price data
    mapping(address => PriceData) private prices;

    /// @notice Mapping of token to RedStone data feed ID
    mapping(address => bytes32) public tokenToDataFeedId;

    /// @notice Authorized price updaters (RedStone relayers)
    mapping(address => bool) public authorizedUpdaters;

    struct PriceData {
        uint256 price;
        uint256 timestamp;
        bool isActive;
    }

    error RedStoneOracle__UnauthorizedUpdater();
    error RedStoneOracle__PriceNotSet();
    error RedStoneOracle__StalePrice();
    error RedStoneOracle__InvalidPrice();

    event DataFeedAdded(address indexed token, bytes32 indexed dataFeedId);
    event UpdaterAuthorized(address indexed updater);
    event UpdaterRevoked(address indexed updater);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Add a token with its RedStone data feed ID
     * @param token Token address
     * @param dataFeedId RedStone data feed identifier
     */
    function addTokenDataFeed(address token, bytes32 dataFeedId) external onlyOwner {
        tokenToDataFeedId[token] = dataFeedId;
        prices[token].isActive = true;
        emit DataFeedAdded(token, dataFeedId);
    }

    /**
     * @notice Authorize a price updater (RedStone relayer)
     * @param updater Address to authorize
     */
    function authorizeUpdater(address updater) external onlyOwner {
        authorizedUpdaters[updater] = true;
        emit UpdaterAuthorized(updater);
    }

    /**
     * @notice Revoke updater authorization
     * @param updater Address to revoke
     */
    function revokeUpdater(address updater) external onlyOwner {
        authorizedUpdaters[updater] = false;
        emit UpdaterRevoked(updater);
    }

    /**
     * @notice Update price for a token (called by RedStone relayers)
     * @param token Token address
     * @param price New price with 18 decimals
     */
    function updatePrice(address token, uint256 price) external {
        if (!authorizedUpdaters[msg.sender]) revert RedStoneOracle__UnauthorizedUpdater();
        if (price == 0) revert RedStoneOracle__InvalidPrice();
        if (!prices[token].isActive) revert RedStoneOracle__PriceNotSet();

        prices[token].price = price;
        prices[token].timestamp = block.timestamp;

        emit PriceUpdated(token, price, block.timestamp);
    }

    /**
     * @notice Get the latest price for a token
     * @param token Token address
     * @return price Price with 18 decimals
     * @return timestamp Timestamp of last update
     */
    function getPrice(address token) external view override returns (uint256 price, uint256 timestamp) {
        PriceData memory priceData = prices[token];

        if (!priceData.isActive) revert RedStoneOracle__PriceNotSet();
        if (block.timestamp - priceData.timestamp > PRICE_STALENESS_THRESHOLD) {
            revert RedStoneOracle__StalePrice();
        }

        return (priceData.price, priceData.timestamp);
    }

    /**
     * @notice Check if token has valid price feed
     * @param token Token address
     * @return hasPrice True if valid price exists
     */
    function hasPrice(address token) external view override returns (bool) {
        PriceData memory priceData = prices[token];
        return priceData.isActive && 
               priceData.price > 0 && 
               (block.timestamp - priceData.timestamp <= PRICE_STALENESS_THRESHOLD);
    }

    /**
     * @notice Get price staleness threshold
     * @return threshold Staleness threshold in seconds
     */
    function getPriceStalenessThreshold() external pure override returns (uint256 threshold) {
        return PRICE_STALENESS_THRESHOLD;
    }
}

