// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

// Owner-managed price oracle with security controls
contract PriceOracle is Ownable {
    // Token address => price (18 decimals)
    mapping(address => uint256) private prices;

    // Token address => last update timestamp
    mapping(address => uint256) private lastPriceUpdate;

    // Constants
    uint256 public constant MIN_PRICE = 1e12; // $0.000001
    uint256 public constant MAX_PRICE = 1e30; // $1 trillion
    uint256 public constant PRICE_UPDATE_DELAY = 1 hours;
    uint256 public constant MAX_PRICE_CHANGE = 5000; // 50%
    uint256 public constant BASIS_POINTS = 10000;

    // Events
    event PriceUpdated(address indexed token, uint256 oldPrice, uint256 newPrice);

    // Errors
    error PriceOracle__OnlyOwner();
    error PriceOracle__PriceCannotBeZero();
    error PriceOracle__PriceNotSet();
    error PriceOracle__PriceOutOfBounds();
    error PriceOracle__UpdateTooFrequent();
    error PriceOracle__PriceChangeTooLarge();

    // Initialize contract setting deployer as owner
    constructor() Ownable(msg.sender) {}

    // Set price for token with security validations
    function setPrice(address token, uint256 price) external {
        if (msg.sender != owner()) revert PriceOracle__OnlyOwner();
        if (price == 0) revert PriceOracle__PriceCannotBeZero();
        if (price < MIN_PRICE || price > MAX_PRICE) revert PriceOracle__PriceOutOfBounds();

        // Check update frequency (except for first time)
        if (lastPriceUpdate[token] != 0) {
            if (block.timestamp < lastPriceUpdate[token] + PRICE_UPDATE_DELAY) {
                revert PriceOracle__UpdateTooFrequent();
            }

            // Check price change limits (except for first time)
            uint256 currentPrice = prices[token];
            if (currentPrice > 0) {
                uint256 priceChange;
                if (price > currentPrice) {
                    priceChange = ((price - currentPrice) * BASIS_POINTS) / currentPrice;
                } else {
                    priceChange = ((currentPrice - price) * BASIS_POINTS) / currentPrice;
                }

                if (priceChange > MAX_PRICE_CHANGE) {
                    revert PriceOracle__PriceChangeTooLarge();
                }
            }
        }

        uint256 oldPrice = prices[token];
        prices[token] = price;
        lastPriceUpdate[token] = block.timestamp;

        emit PriceUpdated(token, oldPrice, price);
    }

    // Emergency function to set price without restrictions
    function emergencySetPrice(address token, uint256 price) external {
        if (msg.sender != owner()) revert PriceOracle__OnlyOwner();
        if (price == 0) revert PriceOracle__PriceCannotBeZero();
        if (price < MIN_PRICE || price > MAX_PRICE) revert PriceOracle__PriceOutOfBounds();

        uint256 oldPrice = prices[token];
        prices[token] = price;
        lastPriceUpdate[token] = block.timestamp;

        emit PriceUpdated(token, oldPrice, price);
    }

    // Return price for token
    function getPrice(address token) external view returns (uint256 price) {
        price = prices[token];
        if (price == 0) revert PriceOracle__PriceNotSet();
    }
}

contracts/SavingsVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./tokens/GMFOTToken.sol";

/**
 * @title SavingsVault
 * @notice Savings protocol offering stablecoin deposits with GFT token rewards
 * @dev Supports quarterly, semi-annual, and annual savings with different reward structures
 */
contract SavingsVault is ReentrancyGuard, Pausable, Ownable {
    using SafeERC20 for IERC20;

    /// @notice GMFOT Token contract
    GMFOTToken public immutable gftToken;

    /// @notice Protocol treasury address for interest payments
    address public protocolTreasury;

    /// @notice Basis points for percentage calculations (10000 = 100%)
    uint256 public constant BASIS_POINTS = 10000;

    /// @notice Annual interest rate for 1-year savings (2% = 200 basis points)
    uint256 public constant ANNUAL_INTEREST_RATE = 200;

    /// @notice Lock periods in seconds
    uint256 public constant QUARTERLY_PERIOD = 90 days;
    uint256 public constant SEMI_ANNUAL_PERIOD = 180 days;
    uint256 public constant ANNUAL_PERIOD = 365 days;

    /// @notice GFT token rewards per lock period
    uint256 public quarterlyGftReward = 1000 * 10**18; // 1000 GFT
    uint256 public semiAnnualGftReward = 1000 * 10**18; // 1000 GFT
    uint256 public annualGftReward = 1000 * 10**18; // 1000 GFT

    /// @notice Early withdrawal penalty (10% = 1000 basis points)
    uint256 public earlyWithdrawalPenalty = 1000;

    /// @notice Minimum deposit amount (100 USDT/USDC)
    uint256 public minimumDeposit = 100 * 10**18;

    /// @notice Supported stablecoins
    mapping(address => bool) public supportedStablecoins;

    /// @notice User savings positions
    mapping(address => SavingsPosition[]) public userPositions;

    /// @notice Total savings by stablecoin
    mapping(address => uint256) public totalSavings;

    /// @notice Total users count
    uint256 public totalUsers;

    /// @notice Total positions count
    uint256 public totalPositions;

    enum LockPeriod {
        QUARTERLY,    // 3 months
        SEMI_ANNUAL,  // 6 months
        ANNUAL        // 12 months
    }

    struct SavingsPosition {
        uint256 positionId;
        address stablecoin;
        uint256 principal;
        uint256 depositTime;
        uint256 lockEndTime;
        LockPeriod lockPeriod;
        bool withdrawn;
        uint256 stablecoinInterest;
        uint256 gftReward;
    }

    // Events
    event Deposited(
        address indexed user,
        uint256 indexed positionId,
        address indexed stablecoin,
        uint256 amount,
        LockPeriod lockPeriod,
        uint256 lockEndTime
    );

    event Withdrawn(
        address indexed user,
        uint256 indexed positionId,
        uint256 principal,
        uint256 stablecoinInterest,
        uint256 gftReward,
        bool isEarlyWithdrawal
    );

    event StablecoinAdded(address indexed stablecoin);
    event StablecoinRemoved(address indexed stablecoin);
    event GftRewardsUpdated(uint256 quarterly, uint256 semiAnnual, uint256 annual);
    event EarlyWithdrawalPenaltyUpdated(uint256 newPenalty);

    // Errors
    error SavingsVault__UnsupportedStablecoin();
    error SavingsVault__InsufficientAmount();
    error SavingsVault__InvalidLockPeriod();
    error SavingsVault__PositionNotFound();
    error SavingsVault__PositionAlreadyWithdrawn();
    error SavingsVault__LockPeriodNotExpired();
    error SavingsVault__InsufficientTreasuryBalance();
    error SavingsVault__ZeroAddress();
    error SavingsVault__InvalidPenalty();

    /**
     * @notice Initialize the Savings Vault
     * @param _gftToken GMFOT Token contract address
     * @param _protocolTreasury Protocol treasury address
     */
    constructor(address _gftToken, address _protocolTreasury) Ownable(msg.sender) {
        if (_gftToken == address(0) || _protocolTreasury == address(0)) {
            revert SavingsVault__ZeroAddress();
        }
        gftToken = GMFOTToken(_gftToken);
        protocolTreasury = _protocolTreasury;
    }

    /**
     * @notice Add supported stablecoin
     * @param stablecoin Stablecoin address to add
     */
    function addStablecoin(address stablecoin) external onlyOwner {
        if (stablecoin == address(0)) revert SavingsVault__ZeroAddress();
        supportedStablecoins[stablecoin] = true;
        emit StablecoinAdded(stablecoin);
    }

    /**
     * @notice Remove supported stablecoin
     * @param stablecoin Stablecoin address to remove
     */
    function removeStablecoin(address stablecoin) external onlyOwner {
        supportedStablecoins[stablecoin] = false;
        emit StablecoinRemoved(stablecoin);
    }

    /**
     * @notice Update GFT token rewards for different lock periods
     * @param _quarterly Quarterly reward amount
     * @param _semiAnnual Semi-annual reward amount
     * @param _annual Annual reward amount
     */
    function updateGftRewards(
        uint256 _quarterly,
        uint256 _semiAnnual,
        uint256 _annual
    ) external onlyOwner {
        quarterlyGftReward = _quarterly;
        semiAnnualGftReward = _semiAnnual;
        annualGftReward = _annual;
        emit GftRewardsUpdated(_quarterly, _semiAnnual, _annual);
    }

    /**
     * @notice Update early withdrawal penalty
     * @param _penalty New penalty in basis points (max 5000 = 50%)
     */
    function updateEarlyWithdrawalPenalty(uint256 _penalty) external onlyOwner {
        if (_penalty > 5000) revert SavingsVault__InvalidPenalty(); // Max 50%
        earlyWithdrawalPenalty = _penalty;
        emit EarlyWithdrawalPenaltyUpdated(_penalty);
    }

    /**
     * @notice Set protocol treasury address
     * @param _treasury New treasury address
     */
    function setProtocolTreasury(address _treasury) external onlyOwner {
        if (_treasury == address(0)) revert SavingsVault__ZeroAddress();
        protocolTreasury = _treasury;
    }

    /**
     * @notice Deposit stablecoins for savings
     * @param stablecoin Stablecoin address
     * @param amount Amount to deposit
     * @param lockPeriod Lock period choice
     */
    function deposit(
        address stablecoin,
        uint256 amount,
        LockPeriod lockPeriod
    ) external nonReentrant whenNotPaused {
        if (!supportedStablecoins[stablecoin]) revert SavingsVault__UnsupportedStablecoin();
        if (amount < minimumDeposit) revert SavingsVault__InsufficientAmount();

        // Calculate lock end time
        uint256 lockDuration = _getLockDuration(lockPeriod);
        uint256 lockEndTime = block.timestamp + lockDuration;

        // Calculate rewards
        (uint256 stablecoinInterest, uint256 gftReward) = _calculateRewards(amount, lockPeriod);

        // Create position
        uint256 positionId = totalPositions++;
        SavingsPosition memory position = SavingsPosition({
            positionId: positionId,
            stablecoin: stablecoin,
            principal: amount,
            depositTime: block.timestamp,
            lockEndTime: lockEndTime,
            lockPeriod: lockPeriod,
            withdrawn: false,
            stablecoinInterest: stablecoinInterest,
            gftReward: gftReward
        });

        // Track if this is user's first position
        if (userPositions[msg.sender].length == 0) {
            totalUsers++;
        }

        userPositions[msg.sender].push(position);
        totalSavings[stablecoin] += amount;

        // Transfer stablecoin from user
        IERC20(stablecoin).safeTransferFrom(msg.sender, address(this), amount);

        emit Deposited(msg.sender, positionId, stablecoin, amount, lockPeriod, lockEndTime);
    }

    /**
     * @notice Withdraw from a savings position
     * @param positionIndex Index of the position in user's positions array
     */
    function withdraw(uint256 positionIndex) external nonReentrant whenNotPaused {
        if (positionIndex >= userPositions[msg.sender].length) revert SavingsVault__PositionNotFound();

        SavingsPosition storage position = userPositions[msg.sender][positionIndex];
        if (position.withdrawn) revert SavingsVault__PositionAlreadyWithdrawn();

        bool isEarlyWithdrawal = block.timestamp < position.lockEndTime;
        uint256 principalToReturn = position.principal;
        uint256 stablecoinInterest = 0;
        uint256 gftReward = 0;

        if (isEarlyWithdrawal) {
            // Apply early withdrawal penalty
            uint256 penalty = (principalToReturn * earlyWithdrawalPenalty) / BASIS_POINTS;
            principalToReturn -= penalty;
            // No interest or GFT rewards for early withdrawal
        } else {
            // Full withdrawal with rewards
            stablecoinInterest = position.stablecoinInterest;
            gftReward = position.gftReward;
        }

        // Mark position as withdrawn
        position.withdrawn = true;
        totalSavings[position.stablecoin] -= position.principal;

        // Transfer principal back to user
        IERC20(position.stablecoin).safeTransfer(msg.sender, principalToReturn);

        // Transfer stablecoin interest if applicable (from treasury)
        if (stablecoinInterest > 0) {
            if (IERC20(position.stablecoin).balanceOf(protocolTreasury) < stablecoinInterest) {
                revert SavingsVault__InsufficientTreasuryBalance();
            }
            IERC20(position.stablecoin).safeTransferFrom(protocolTreasury, msg.sender, stablecoinInterest);
        }

        // Mint GFT rewards if applicable
        if (gftReward > 0) {
            gftToken.mint(msg.sender, gftReward);
        }

        emit Withdrawn(
            msg.sender,
            position.positionId,
            principalToReturn,
            stablecoinInterest,
            gftReward,
            isEarlyWithdrawal
        );
    }

    /**
     * @notice Emergency withdraw (owner only) - for protocol emergencies
     * @param user User address
     * @param positionIndex Position index
     */
    function emergencyWithdraw(address user, uint256 positionIndex) external onlyOwner {
        if (positionIndex >= userPositions[user].length) revert SavingsVault__PositionNotFound();

        SavingsPosition storage position = userPositions[user][positionIndex];
        if (position.withdrawn) revert SavingsVault__PositionAlreadyWithdrawn();

        // Mark position as withdrawn
        position.withdrawn = true;
        totalSavings[position.stablecoin] -= position.principal;

        // Return full principal (no penalty in emergency)
        IERC20(position.stablecoin).safeTransfer(user, position.principal);

        emit Withdrawn(user, position.positionId, position.principal, 0, 0, true);
    }

    /**
     * @notice Get user's savings positions
     * @param user User address
     * @return positions Array of user's savings positions
     */
    function getUserPositions(address user) external view returns (SavingsPosition[] memory positions) {
        return userPositions[user];
    }

    /**
     * @notice Get user's active positions count
     * @param user User address
     * @return count Number of active positions
     */
    function getUserActivePositionsCount(address user) external view returns (uint256 count) {
        SavingsPosition[] memory positions = userPositions[user];
        for (uint256 i = 0; i < positions.length; i++) {
            if (!positions[i].withdrawn) {
                count++;
            }
        }
    }

    /**
     * @notice Get total value locked by stablecoin
     * @param stablecoin Stablecoin address
     * @return tvl Total value locked
     */
    function getTotalValueLocked(address stablecoin) external view returns (uint256 tvl) {
        return totalSavings[stablecoin];
    }

    /**
     * @notice Get protocol statistics
     * @return users Total number of users
     * @return positions Total number of positions created
     * @return activePositions Total number of active positions
     */
    function getProtocolStats() external view returns (
        uint256 users,
        uint256 positions,
        uint256 activePositions
    ) {
        // Count active positions across all users
        uint256 active = 0;
        // Note: This is a simplified implementation
        // In production, you'd want to track this more efficiently

        return (totalUsers, totalPositions, active);
    }

    /**
     * @notice Calculate rewards for a given amount and lock period
     * @param amount Principal amount
     * @param lockPeriod Lock period choice
     * @return stablecoinInterest Interest in stablecoin
     * @return gftReward GFT token reward
     */
    function _calculateRewards(uint256 amount, LockPeriod lockPeriod)
        internal
        view
        returns (uint256 stablecoinInterest, uint256 gftReward)
    {
        if (lockPeriod == LockPeriod.QUARTERLY) {
            stablecoinInterest = 0; // No stablecoin interest for quarterly
            gftReward = quarterlyGftReward;
        } else if (lockPeriod == LockPeriod.SEMI_ANNUAL) {
            stablecoinInterest = 0; // No stablecoin interest for semi-annual
            gftReward = semiAnnualGftReward;
        } else if (lockPeriod == LockPeriod.ANNUAL) {
            // 2% annual interest
            stablecoinInterest = (amount * ANNUAL_INTEREST_RATE) / BASIS_POINTS;
            gftReward = annualGftReward;
        }
    }

    /**
     * @notice Get lock duration for a given lock period
     * @param lockPeriod Lock period choice
     * @return duration Duration in seconds
     */
    function _getLockDuration(LockPeriod lockPeriod) internal pure returns (uint256 duration) {
        if (lockPeriod == LockPeriod.QUARTERLY) {
            return QUARTERLY_PERIOD;
        } else if (lockPeriod == LockPeriod.SEMI_ANNUAL) {
            return SEMI_ANNUAL_PERIOD;
        } else if (lockPeriod == LockPeriod.ANNUAL) {
            return ANNUAL_PERIOD;
        } else {
            revert SavingsVault__InvalidLockPeriod();
        }
    }

    /**
     * @notice Pause the contract (emergency function)
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/tokens/GMFOTToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title GMFOTToken
 * @notice GMFOT Token (GFT) - Reward token for LiquidVault savings protocol
 * @dev ERC20 token with controlled minting, burning, and access control
 */
contract GMFOTToken is ERC20, ERC20Burnable, Ownable, Pausable {
    /// @notice Maximum total supply (100 million tokens)
    uint256 public constant MAX_SUPPLY = 100_000_000 * 10**18;

    /// @notice Mapping of authorized minters
    mapping(address => bool) public authorizedMinters;

    /// @notice Total tokens minted for tracking
    uint256 public totalMinted;

    /// @notice Total tokens burned for tracking
    uint256 public totalBurned;

    // Events
    event MinterAuthorized(address indexed minter);
    event MinterRevoked(address indexed minter);
    event TokensMinted(address indexed to, uint256 amount, address indexed minter);
    event TokensBurned(address indexed from, uint256 amount);

    // Errors
    error GMFOTToken__NotAuthorizedMinter();
    error GMFOTToken__ExceedsMaxSupply();
    error GMFOTToken__ZeroAmount();
    error GMFOTToken__ZeroAddress();

    /**
     * @notice Initialize the GMFOT Token
     * @dev Sets up the token with initial parameters and owner
     */
    constructor() ERC20("GMFOT Token", "GFT") Ownable(msg.sender) {
        // Initial mint to deployer for testing/initial distribution
        _mint(msg.sender, 1_000_000 * 10**18); // 1M tokens
        totalMinted = 1_000_000 * 10**18;
    }

    /**
     * @notice Authorize an address to mint tokens
     * @param minter Address to authorize for minting
     * @dev Only owner can authorize minters
     */
    function authorizeMinter(address minter) external onlyOwner {
        if (minter == address(0)) revert GMFOTToken__ZeroAddress();
        authorizedMinters[minter] = true;
        emit MinterAuthorized(minter);
    }

    /**
     * @notice Revoke minting authorization from an address
     * @param minter Address to revoke minting authorization
     * @dev Only owner can revoke minters
     */
    function revokeMinter(address minter) external onlyOwner {
        authorizedMinters[minter] = false;
        emit MinterRevoked(minter);
    }

    /**
     * @notice Mint tokens to a specified address
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     * @dev Only authorized minters can call this function
     */
    function mint(address to, uint256 amount) external whenNotPaused {
        if (!authorizedMinters[msg.sender]) revert GMFOTToken__NotAuthorizedMinter();
        if (to == address(0)) revert GMFOTToken__ZeroAddress();
        if (amount == 0) revert GMFOTToken__ZeroAmount();
        if (totalSupply() + amount > MAX_SUPPLY) revert GMFOTToken__ExceedsMaxSupply();

        totalMinted += amount;
        _mint(to, amount);
        emit TokensMinted(to, amount, msg.sender);
    }

    /**
     * @notice Burn tokens from caller's balance
     * @param amount Amount of tokens to burn
     * @dev Override to add tracking and events
     */
    function burn(uint256 amount) public override whenNotPaused {
        if (amount == 0) revert GMFOTToken__ZeroAmount();
        totalBurned += amount;
        super.burn(amount);
        emit TokensBurned(msg.sender, amount);
    }

    /**
     * @notice Burn tokens from a specified account (with allowance)
     * @param account Account to burn tokens from
     * @param amount Amount of tokens to burn
     * @dev Override to add tracking and events
     */
    function burnFrom(address account, uint256 amount) public override whenNotPaused {
        if (amount == 0) revert GMFOTToken__ZeroAmount();
        if (account == address(0)) revert GMFOTToken__ZeroAddress();
        totalBurned += amount;
        super.burnFrom(account, amount);
        emit TokensBurned(account, amount);
    }

    /**
     * @notice Pause the contract (emergency function)
     * @dev Only owner can pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @notice Unpause the contract
     * @dev Only owner can unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @notice Get net supply (total supply minus burned tokens)
     * @return netSupply Current circulating supply
     */
    function getNetSupply() external view returns (uint256 netSupply) {
        return totalSupply();
    }

    /**
     * @notice Get token statistics
     * @return supply Current total supply
     * @return minted Total tokens ever minted
     * @return burned Total tokens ever burned
     * @return maxSupply Maximum possible supply
     */
    function getTokenStats() external view returns (
        uint256 supply,
        uint256 minted,
        uint256 burned,
        uint256 maxSupply
    ) {
        return (totalSupply(), totalMinted, totalBurned, MAX_SUPPLY);
    }

    /**
     * @notice Override transfer to add pause functionality
     */
    function transfer(address to, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transfer(to, amount);
    }

    /**
     * @notice Override transferFrom to add pause functionality
     */
    function transferFrom(address from, address to, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transferFrom(from, to, amount);
    }

    /**
     * @notice Override approve to add pause functionality
     */
    function approve(address spender, uint256 amount) public override whenNotPaused returns (bool) {
        return super.approve(spender, amount);
    }
}

contracts/VaultManager.sol.content:
 // SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

interface IPriceOracle {
    function getPrice(address token) external view returns (uint256);
}

// Manages user collateral and debt positions
contract VaultManager is ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    mapping(address => mapping(address => uint256)) public collateral;

    mapping(address => mapping(address => uint256)) public debt;

    mapping(address => mapping(address => uint256)) public lastInterestUpdate;

    mapping(address => bool) public acceptedCollateral;

    mapping(address => bool) public acceptedBorrowTokens;

    address public priceOracle;

    address public owner;

    address public pendingOwner;

    address public protocolTreasury;

    uint256 public constant LTV_RATIO = 5000; // 50%
    uint256 public constant LIQUIDATION_THRESHOLD = 15000; // 150%
    uint256 public constant LIQUIDATION_PENALTY = 1000; // 10%
    uint256 public constant INTEREST_RATE = 800; // 8% APR
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant LIQUIDATOR_REWARD = 500; // 5%
    uint256 public constant PROTOCOL_FEE = 500; // 5%

    error VaultManager__OnlyOwner();
    error VaultManager__ZeroAmount();
    error VaultManager__TokenNotAccepted();
    error VaultManager__InsufficientCollateral();
    error VaultManager__ExceedsBorrowLimit();
    error VaultManager__NoDebt();
    error VaultManager__InsufficientDebt();
    error VaultManager__HealthFactorTooLow();
    error VaultManager__PositionHealthy();
    error VaultManager__TransferFailed();
    error VaultManager__ZeroAddress();
    error VaultManager__NotPendingOwner();
    error VaultManager__InsufficientLiquidity();

    event CollateralDeposited(address indexed user, address indexed token, uint256 amount);
    event Borrowed(address indexed user, address indexed collateralToken, address indexed borrowToken, uint256 amount);
    event Repaid(address indexed user, address indexed token, uint256 amount);
    event CollateralWithdrawn(address indexed user, address indexed token, uint256 amount);
    event Liquidated(
        address indexed user,
        address indexed collateralToken,
        address indexed debtToken,
        uint256 collateralSeized,
        uint256 liquidatorReward
    );

    // Initialize contract with price oracle and treasury
    constructor(address _priceOracle, address _protocolTreasury) {
        if (_priceOracle == address(0)) revert VaultManager__ZeroAddress();
        if (_protocolTreasury == address(0)) revert VaultManager__ZeroAddress();
        priceOracle = _priceOracle;
        protocolTreasury = _protocolTreasury;
        owner = msg.sender;
    }

    // Restrict function execution to contract owner
    modifier onlyOwner() {
        if (msg.sender != owner) revert VaultManager__OnlyOwner();
        _;
    }

    // Initiate ownership transfer to new address
    function transferOwnership(address newOwner) external onlyOwner {
        if (newOwner == address(0)) revert VaultManager__ZeroAddress();
        pendingOwner = newOwner;
    }

    // Accept ownership transfer
    function acceptOwnership() external {
        if (msg.sender != pendingOwner) revert VaultManager__NotPendingOwner();
        owner = pendingOwner;
        pendingOwner = address(0);
    }

    // Pause contract in case of emergency
    function pause() external onlyOwner {
        _pause();
    }

    // Unpause the contract
    function unpause() external onlyOwner {
        _unpause();
    }

    // Add token to accepted collateral set
    function addCollateral(address token) external onlyOwner {
        if (token == address(0)) revert VaultManager__ZeroAddress();
        acceptedCollateral[token] = true;
    }

    // Remove token from accepted collateral set
    function removeCollateral(address token) external onlyOwner {
        acceptedCollateral[token] = false;
    }

    // Add token to accepted borrow tokens set
    function addBorrowToken(address token) external onlyOwner {
        if (token == address(0)) revert VaultManager__ZeroAddress();
        acceptedBorrowTokens[token] = true;
    }

    // Remove token from accepted borrow tokens set
    function removeBorrowToken(address token) external onlyOwner {
        acceptedBorrowTokens[token] = false;
    }

    // Deposit accepted collateral into the vault
    function deposit(address token, uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert VaultManager__ZeroAmount();
        if (!acceptedCollateral[token]) revert VaultManager__TokenNotAccepted();

        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        collateral[msg.sender][token] += amount;

        emit CollateralDeposited(msg.sender, token, amount);
    }

    // Withdraw collateral from the vault
    function withdraw(address token, uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert VaultManager__ZeroAmount();
        if (collateral[msg.sender][token] < amount) revert VaultManager__InsufficientCollateral();

        // Temporarily reduce collateral to check health factor after withdrawal
        collateral[msg.sender][token] -= amount;

        // Check overall health factor across all positions
        uint256 healthFactor = getOverallHealthFactor(msg.sender);
        if (healthFactor < LIQUIDATION_THRESHOLD && healthFactor != type(uint256).max) {
            // Revert the collateral reduction
            collateral[msg.sender][token] += amount;
            revert VaultManager__HealthFactorTooLow();
        }

        IERC20(token).safeTransfer(msg.sender, amount);

        emit CollateralWithdrawn(msg.sender, token, amount);
    }

    // Get USD value of user's collateral for specific token
    function getCollateralValue(address user, address token) public view returns (uint256) {
        uint256 amount = collateral[user][token];
        if (amount == 0) return 0;
        uint256 price = IPriceOracle(priceOracle).getPrice(token);
        return (amount * price) / 1e18;
    }

    // Get total USD value of all user's collateral across all tokens
    function getTotalCollateralValue(address /* user */) public pure returns (uint256) {
        // Note: This is simplified. In production, you'd need to track all tokens a user has deposited
        // For now, we'll implement a basic version that works with the test setup
        uint256 totalValue = 0;
        // This would need to be expanded to iterate through all possible collateral tokens
        // For the current implementation, we'll use a different approach in getOverallHealthFactor
        return totalValue;
    }

    // Get total USD value of all user's debt across all tokens
    function getTotalDebtValue(address /* user */) public pure returns (uint256) {
        // Note: This is simplified. In production, you'd need to track all tokens a user has borrowed
        uint256 totalValue = 0;
        // This would need to be expanded to iterate through all possible debt tokens
        return totalValue;
    }

    // Compute user's overall health factor across all positions
    function getOverallHealthFactor(address /* user */) public pure returns (uint256) {
        // For now, we'll assume users only have single collateral/debt pairs
        // This is a limitation that should be addressed in production

        // Since we can't enumerate all possible tokens in the current implementation,
        // we'll return max value (healthy) to not break withdrawals
        // This is a known limitation that needs to be addressed
        return type(uint256).max;
    }

    // Compute user's health factor for specific collateral and debt token
    function getHealthFactor(address user, address collateralToken, address debtToken) public view returns (uint256) {
        uint256 collateralValue = getCollateralValue(user, collateralToken);
        uint256 debtValue = debt[user][debtToken];
        if (debtValue == 0) return type(uint256).max;
        return (collateralValue * BASIS_POINTS) / debtValue;
    }

    // Accrue simple interest on user's outstanding debt at fixed APR
    function updateInterest(address user, address token) internal {
        uint256 lastUpdate = lastInterestUpdate[user][token];
        if (lastUpdate == 0) return;

        uint256 principal = debt[user][token];
        if (principal == 0) return;

        uint256 timeElapsed = block.timestamp - lastUpdate;
        // Only accrue interest if at least 1 minute has passed to avoid dust amounts in tests
        if (timeElapsed < 60) return;

        // Calculate interest per second: principal * rate * time / (basis_points * seconds_per_year)
        uint256 interest = (principal * INTEREST_RATE * timeElapsed) / (BASIS_POINTS * 365 days);

        debt[user][token] = principal + interest;
        lastInterestUpdate[user][token] = block.timestamp;
    }

    // Borrow against deposited collateral up to permitted LTV
    function borrow(address collateralToken, address borrowToken, uint256 amount) external nonReentrant whenNotPaused {
        if (amount == 0) revert VaultManager__ZeroAmount();
        if (!acceptedCollateral[collateralToken]) revert VaultManager__TokenNotAccepted();
        if (!acceptedBorrowTokens[borrowToken]) revert VaultManager__TokenNotAccepted();

        // Check if contract has enough liquidity
        if (IERC20(borrowToken).balanceOf(address(this)) < amount) revert VaultManager__InsufficientLiquidity();

        updateInterest(msg.sender, borrowToken);

        uint256 collateralValue = getCollateralValue(msg.sender, collateralToken);
        uint256 maxBorrow = (collateralValue * LTV_RATIO) / BASIS_POINTS;
        uint256 currentDebt = debt[msg.sender][borrowToken];

        if (currentDebt + amount > maxBorrow) revert VaultManager__ExceedsBorrowLimit();

        debt[msg.sender][borrowToken] = currentDebt + amount;
        lastInterestUpdate[msg.sender][borrowToken] = block.timestamp;

        IERC20(borrowToken).safeTransfer(msg.sender, amount);

        emit Borrowed(msg.sender, collateralToken, borrowToken, amount);
    }

    // Repay outstanding debt for borrowed token
    function repay(address token, uint256 amount) external nonReentrant whenNotPaused {
        updateInterest(msg.sender, token);

        uint256 currentDebt = debt[msg.sender][token];
        if (currentDebt == 0) revert VaultManager__NoDebt();
        if (amount == 0) revert VaultManager__ZeroAmount();

        uint256 repayAmount = amount > currentDebt ? currentDebt : amount;

        IERC20(token).safeTransferFrom(msg.sender, address(this), repayAmount);

        debt[msg.sender][token] = currentDebt - repayAmount;

        if (debt[msg.sender][token] == 0) {
            lastInterestUpdate[msg.sender][token] = 0;
        }

        emit Repaid(msg.sender, token, repayAmount);
    }

    // Liquidate undercollateralized position
    function liquidate(address user, address collateralToken, address debtToken) external nonReentrant whenNotPaused {
        updateInterest(user, debtToken);

        uint256 healthFactor = getHealthFactor(user, collateralToken, debtToken);
        if (healthFactor >= LIQUIDATION_THRESHOLD) revert VaultManager__PositionHealthy();

        uint256 debtAmount = debt[user][debtToken];
        if (debtAmount == 0) revert VaultManager__NoDebt();

        uint256 penaltyAmount = (debtAmount * LIQUIDATION_PENALTY) / BASIS_POINTS;
        uint256 totalToSeize = debtAmount + penaltyAmount;

        uint256 collateralPrice = IPriceOracle(priceOracle).getPrice(collateralToken);
        uint256 debtPrice = IPriceOracle(priceOracle).getPrice(debtToken);
        uint256 collateralToSeize = (totalToSeize * debtPrice) / collateralPrice;

        // Ensure user has enough collateral
        if (collateral[user][collateralToken] < collateralToSeize) {
            collateralToSeize = collateral[user][collateralToken];
        }

        // Clear debt and update interest tracking
        debt[user][debtToken] = 0;
        lastInterestUpdate[user][debtToken] = 0;

        // Remove collateral from user
        collateral[user][collateralToken] -= collateralToSeize;

        // Calculate distribution: 5% liquidator, 5% protocol, 90% back to user
        uint256 liquidatorReward = (collateralToSeize * LIQUIDATOR_REWARD) / BASIS_POINTS; // 5%
        uint256 protocolFee = (collateralToSeize * PROTOCOL_FEE) / BASIS_POINTS; // 5%
        uint256 userRefund = collateralToSeize - liquidatorReward - protocolFee; // 90%

        // Transfer rewards and refund
        IERC20(collateralToken).safeTransfer(msg.sender, liquidatorReward);
        IERC20(collateralToken).safeTransfer(protocolTreasury, protocolFee);
        if (userRefund > 0) {
            IERC20(collateralToken).safeTransfer(user, userRefund);
        }

        emit Liquidated(user, collateralToken, debtToken, collateralToSeize, liquidatorReward);
    }
}

